package uz.orifjon.newsappdagger2.fragments.tutorial.fragments

import android.os.Bundle
import android.os.Handler
import android.view.View
import androidx.core.view.doOnAttach
import androidx.fragment.app.Fragment
import androidx.navigation.fragment.findNavController
import androidx.viewpager2.widget.ViewPager2
import uz.orifjon.newsappdagger2.R
import uz.orifjon.newsappdagger2.adapters.OnBoardingAdapter
import uz.orifjon.newsappdagger2.databinding.FragmentOnBoardingBinding
import uz.orifjon.newsappdagger2.utils.HorizontalMarginItemDecoration
import uz.orifjon.newsappdagger2.utils.viewBindings

class OnBoardingFragment : Fragment(R.layout.fragment_on_boarding) {

    private val binding by viewBindings(FragmentOnBoardingBinding::bind)
    private val viewpagerAdapter by lazy {
        OnBoardingAdapter()
    }

    private var list = emptyList<String>()

    private var handler: Handler? = null
    private var update: Runnable? = null

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        loadData()

        viewpagerAdapter.setData(list)

        binding.viewPager2.adapter = viewpagerAdapter

        binding.nextBtn.setOnClickListener {
            findNavController().navigate(R.id.action_onBoardingFragment_to_welcomeScreenFragment)
        }
        binding.dotIndicator.attachTo(binding.viewPager2)

        binding.viewPager2.doOnAttach {
            binding.viewPager2.setCurrentItem(1, false)
        }
    }

    private fun loadData() {
        list = arrayListOf(
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,"
        )

        setUpCarousel()

    }


    private fun setUpCarousel() {

        binding.viewPager2.offscreenPageLimit = 1

        val nextItemVisiblePx = resources.getDimension(R.dimen.viewpager_next_item_visible)
        val currentItemHorizontalMarginPx =
            resources.getDimension(R.dimen.viewpager_current_item_horizontal_margin)
        val pageTranslationX = nextItemVisiblePx + currentItemHorizontalMarginPx
        val pageTransformer = ViewPager2.PageTransformer { page: View, position: Float ->
            page.translationX = -pageTranslationX * position
//            // Next line scales the item's height. You can remove it if you don't want this effect
            page.scaleY = 1 - (0.25f * kotlin.math.abs(position))
//            // If you want a fading effect uncomment the next line:
            page.alpha = 0.25f + (1 - kotlin.math.abs(position))
        }
        binding.viewPager2.setPageTransformer(pageTransformer)

        val itemDecoration = HorizontalMarginItemDecoration(
            requireContext(),
            R.dimen.viewpager_current_item_horizontal_margin
        )
        binding.viewPager2.addItemDecoration(itemDecoration)

    }

}